import React from "react";
import { motion } from "framer-motion";

// Professional Full-Stack Developer Portfolio (React + Tailwind + Framer Motion)
// Sleek corporate layout with smooth animations.

const projects = [
  {
    title: "Weather App",
    desc: "Real-time weather data using OpenWeather API.",
    tech: "React, LocalStorage",
    github: "https://github.com/Dasarad007/Weather-App",
    live: "https://react-js-grr8.vercel.app/",
  },
  {
    title: "E-Commerce App",
    desc: "Product listing, cart management, and responsive design.",
    tech: "React, LocalStorage",
    github: "https://github.com/Dasarad007/ecommerce",
    live: "https://ecommerce-bhbs.vercel.app/",
  },
  {
    title: "Food Store App",
    desc: "Add to cart, remove items, and notifications.",
    tech: "React, LocalStorage",
    github: "https://github.com/Dasarad007/food-project",
    live: "https://food-project-395i.vercel.app/",
  },
  {
    title: "Programiz App",
    desc: "Routing and navigation using React Router.",
    tech: "React, Router",
    github: "https://github.com/Dasarad007/PROGRAMIZ",
    live: "https://programiz-cofx.vercel.app/",
  },
  {
    title: "CRUD App",
    desc: "Full CRUD operations using React, Node.js, Express, and MongoDB.",
    tech: "React, Node.js, Express, MongoDB",
    github: "https://github.com/Dasarad007/CRUD",
    live: "https://crud-ueps.onrender.com",
  },
  {
    title: "REST API",
    desc: "RESTful API for books and products built with Express and MongoDB.",
    tech: "Node.js, Express, MongoDB",
    github: "https://github.com/Dasarad007/REST",
    live: "https://rest-4-cltv.onrender.com",
  },
  {
    title: "Register/Login System",
    desc: "User authentication system using JWT and Bcrypt.",
    tech: "Node.js, Express, MongoDB",
    github: "https://github.com/Dasarad007/register-login",
    live: "https://register-login-n11q.onrender.com",
  },
];

export default function Portfolio() {
  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 text-gray-900 font-sans">
      {/* Header */}
      <motion.header
        initial={{ opacity: 0, y: -40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="max-w-6xl mx-auto px-6 py-8 flex flex-col md:flex-row items-center justify-between"
      >
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dasarad Nagulapalli</h1>
          <p className="text-sm mt-1 text-gray-700">Full-Stack MERN Developer</p>
          <p className="text-sm text-gray-500 mt-1">Rajahmundry, Andhra Pradesh • ndasarad@gmail.com • 9985492934</p>
          <div className="flex gap-4 mt-2 text-sm">
            <a href="https://www.linkedin.com/in/dasarad" className="text-blue-600 hover:underline">LinkedIn</a>
            <a href="https://github.com/Dasarad007" className="text-blue-600 hover:underline">GitHub</a>
            <a href="/Dasarad_Nagulapalli_FrontEnd_Developer_Resume.pdf" className="text-blue-600 hover:underline">Download Full-Stack Developer Resume</a>
          </div>
        </div>
      </motion.header>

      {/* Hero Section */}
      <motion.section
        initial={{ opacity: 0, y: 40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
        className="max-w-6xl mx-auto px-6 py-10 text-center"
      >
        <h2 className="text-2xl md:text-3xl font-semibold text-gray-800">Building Scalable and Modern Web Applications with the MERN Stack</h2>
        <p className="mt-4 text-gray-600 max-w-3xl mx-auto">
          I’m a passionate Full-Stack Developer who specializes in developing efficient, user-centric, and scalable web applications using React, Node.js, Express, and MongoDB. I focus on crafting clean code, optimizing performance, and delivering professional-grade digital experiences.
        </p>
      </motion.section>

      {/* Skills Section */}
      <motion.section
        id="skills"
        initial={{ opacity: 0, y: 40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
        className="max-w-6xl mx-auto px-6 py-10"
      >
        <h3 className="text-xl font-semibold mb-4">Technical Skills</h3>
        <div className="grid md:grid-cols-3 gap-6">
          {[
            {
              title: "Frontend",
              items: [
                "React.js • Redux • JavaScript (ES6+)",
                "HTML5 • CSS3 • Tailwind CSS • Bootstrap",
                "Responsive Design & UI Optimization",
              ],
            },
            {
              title: "Backend & Database",
              items: [
                "Node.js • Express.js",
                "MongoDB • Mongoose",
                "JWT Auth • REST API Design",
              ],
            },
            {
              title: "Tools & Platforms",
              items: [
                "Git • GitHub • VS Code",
                "Postman • Render • Vercel • Netlify",
                "Deployment & Version Control",
              ],
            },
          ].map((category) => (
            <motion.div
              key={category.title}
              whileHover={{ scale: 1.03 }}
              className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition"
            >
              <h4 className="font-semibold text-gray-800">{category.title}</h4>
              <ul className="mt-3 text-sm text-gray-700 space-y-1">
                {category.items.map((item) => (
                  <li key={item}>{item}</li>
                ))}
              </ul>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Projects Section */}
      <motion.section
        id="projects"
        initial={{ opacity: 0, y: 40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
        className="max-w-6xl mx-auto px-6 py-10"
      >
        <h3 className="text-xl font-semibold mb-6">Projects</h3>
        <div className="grid md:grid-cols-2 gap-6">
          {projects.map((p) => (
            <motion.div
              key={p.title}
              whileHover={{ scale: 1.02 }}
              className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition border border-gray-100"
            >
              <h4 className="font-semibold text-gray-800">{p.title}</h4>
              <p className="text-sm text-gray-600 mt-2">{p.desc}</p>
              <p className="text-xs text-gray-500 mt-2">Tech Stack: {p.tech}</p>
              <div className="mt-3 flex gap-3 text-sm">
                <a href={p.github} target="_blank" rel="noreferrer" className="text-blue-600 hover:underline">GitHub</a>
                {p.live && <a href={p.live} target="_blank" rel="noreferrer" className="text-blue-600 hover:underline">Live</a>}
              </div>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Contact Section */}
      <motion.section
        id="contact"
        initial={{ opacity: 0, y: 40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
        className="max-w-6xl mx-auto px-6 py-10 text-center"
      >
        <h3 className="text-xl font-semibold">Contact</h3>
        <p className="text-gray-700 mt-2">Email: <a href="mailto:ndasarad@gmail.com" className="text-blue-600 underline">ndasarad@gmail.com</a></p>
        <p className="text-gray-700 mt-1">Phone: 9985492934</p>
        <p className="text-gray-600 mt-3">Available for Full-Stack roles & open to relocation.</p>
      </motion.section>

      {/* Footer */}
      <footer className="text-center text-gray-500 text-sm py-8 border-t mt-6">
        © {new Date().getFullYear()} Dasarad Nagulapalli — Built with React, Tailwind CSS & Framer Motion
      </footer>
    </div>
  );
}
